INSERT INTO mentors (user_id,description,speciality,location,language) VALUES (1, 'I am a full-stack JavaScript Software Engineer with backend focus with 6+ years of experience. I have a bachelors degree in Computer Science and a postgraduate diploma in Wireless and Mobile Technologies. I am a full-time employee at Chipper Cash.', 'Javascript', 'Toronto', 'English');
INSERT INTO mentors (user_id,description,speciality,location,language) VALUES (2, 'Computer Science graduate with an education technology specialty, I worked mostly as web/mobile developer. I have worked as a web developer both as a freelancer and a permanent employee. I had the opportunity to work on projects of different kinds; new projects with completely new tools which I had to learn on the job, legacy projects where I had to learn about obsolete technology and make it behave like a modern one, young projects to which I had to contribute in short terms, etc...', 'CSS', 'Ottawa', 'English' );
INSERT INTO mentors (user_id,description,speciality,location,language) VALUES (3, 'Over the past two years, I have mentored 20+ people through their early software careers -- from improving their technical skills, to planning independent projects, understanding technical interviews, and conducting an effective job search.My mentees have come from all sorts of backgrounds, including mid-career industry switchers, others with traditional CS backgrounds, and some just out of high school. I have had a variety of tech roles over the past decade, spanning full-stack software engineering, visual and UX design, sales, and solutions engineering -- sometimes as an employee, other times as a freelancer, at companies ranging from seed stage startup, to unicorn startup, to Fortune 500. I also co-founded a bootstrapped startup and served as the sole technical founder.', 'UI Design', 'Montreal', 'French' );
INSERT INTO mentors (user_id,description,speciality,location,language) VALUES (4, 'I am a software engineer currently working at CodeCapi. I started into the Tech Industry 4 years ago, and today I am responsible for web application or mobile application mainly with React / Angular and Nodejs. I am Looking forward to supporting mentees and learning from them.', 'ReactJs', 'Toronto', 'English');
